'use strict';
$(function () {
    var sliderImage = $('.slider__image');
    sliderImage.css({
        height: $(window).width()* 0.371
    });
    
    var Page = (function() {
        var navArrows = jQuery( '#nav-arrows' ).hide(),
            shadow = jQuery( '#shadow' ).hide(),
            slicebox = jQuery( '#sb-slider' ).slicebox( {
                onReady : function() {
                    navArrows.show();
                    shadow.show();
                },
                orientation : 'r',
                cuboidsRandom : true,
                disperseFactor : 30
            } ),

            init = function() {
                initEvents();
            },
            initEvents = function() {
                // add navigation events
                navArrows.children( ':first' ).on( 'click', function() {
                    slicebox.next();
                    return false;
                } );

                navArrows.children( ':last' ).on( 'click', function() {
                    slicebox.previous();
                    return false;
                } );

            };

        return { init : init };
    })();

    Page.init();

    $(window).resize(function () {
        sliderImage.css({
            height: $(window).width()* 0.371
        });
    })
});
$(function () {

    $('.menu__mobile').on('click', function () {
        var list = $('.menu__list');
        var logo = $('.header__logo');
        console.log('calc(50% - '+ logo.width()/2+')');
        if(list.is(':visible')){
            list.hide('slow');
            if($(window).width()< 479){
                logo.css({left: 16, opacity: 1});
            } else {
                logo.css({left: 16});
            }
        } else {
            list.show('slow');
            if($(window).width()< 479){
                logo.css({left: 'calc(50% - '+ logo.width()/2+'px)', opacity: 0});
            } else {
                logo.css({left: 'calc(50% - '+ logo.width()/2+'px)'});
            }
        }
    });

});